!function(l) {
    "use strict";

    // Sembunyikan sidebar pada saat halaman pertama kali dimuat tanpa menambahkan kelas 'toggled'
    l(".sidebar").hide(); // Sembunyikan sidebar pada load

    // Toggle the side navigation
    l("#sidebarToggle, #sidebarToggleTop").on("click", function(e) {
      // Toggle class 'sidebar-toggled' on body
      l("body").toggleClass("sidebar-toggled");

      // Tampilkan atau sembunyikan sidebar dengan toggle
      if (l(".sidebar").is(":visible")) {
        l(".sidebar").hide(); // Sembunyikan sidebar saat diklik lagi
      } else {
        l(".sidebar").show(); // Tampilkan sidebar saat diklik pertama kali
        l(".sidebar .collapse").collapse("hide");
      }
    });

    // Close any open menu accordions when window is resized below 768px
    l(window).resize(function() {
      if (l(window).width() < 768) {
        l(".sidebar .collapse").collapse("hide");
      }

      // Toggle the side navigation when window is resized below 480px
      if (l(window).width() < 480 && !l(".sidebar").is(":visible")) {
        l("body").addClass("sidebar-toggled");
        l(".sidebar").hide(); // Tetap sembunyikan saat resize
        l(".sidebar .collapse").collapse("hide");
      }
    });

    // Handle sidebar scroll behavior
    l("body.fixed-nav .sidebar").on("mousewheel DOMMouseScroll wheel", function(e) {
      var o;
      if (l(window).width() > 768) {
        o = (o = e.originalEvent).wheelDelta || -o.detail;
        this.scrollTop += 30 * (o < 0 ? 1 : -1);
        e.preventDefault();
      }
    });

    // Show/hide scroll to top button
    l(document).on("scroll", function() {
      l(this).scrollTop() > 100 ? l(".scroll-to-top").fadeIn() : l(".scroll-to-top").fadeOut();
    });

    // Smooth scroll to top
    l(document).on("click", "a.scroll-to-top", function(e) {
      var o = l(this);
      l("html, body").stop().animate({ scrollTop: l(o.attr("href")).offset().top }, 1e3, "easeInOutExpo");
      e.preventDefault();
    });

  }(jQuery);

